import styled from "@emotion/styled"

export const Container = styled.div`
  width: 100vw
  height 100vh

  font-family: "Avenir Next";
  font-size: 4vh;
  display: flex;

  flex-direction: column;
  text-decoration: none;

  align-items:center;
`;

export const Banner = styled.div`
  height: 20vh;
  width: 100%;

  background: #c3acce;

  font-family: "Avenir Next";
  font-size: 8vh;
  font-style: bold;

  align-items:center;
  text-align:center;

  display: flex;
`;


export const Header = styled.h1`
  font-size: 8vh;
  font-style: bold;
`;

export const RangeBanner = styled.div`
  height: 10vh;
  width: 100%;
`;

export const Button = styled.div`
height: 50px;
width: 200px;
font-size: 30px;

border-radius: 10px;
background: #ffdab9;
color: #ffffff;

text-align: center;


`;
